('plugin.audio.mixcloud', 'MixCloud', 'jackyNIX/Bochi', '1.0.1', 'Mixcloud is re-thinking radio. Listen to great radio shows, Podcasts and DJ mix sets on-demand.', '2012-10-06', '2012-10-06', NULL, NULL, NULL, NULL, 8754),
('plugin.video.offene_kanaele', 'Offene Kanaele', 'OK Fan', '1.0', '"Public-access television is a form of non-commercial mass media where ordinary people can create content television programming which is cablecast through cable TV specialty channels." (Source: http://en.wikipedia.org/wiki/Public-access_television). This add-on delivers video content from public-access channels in German language.', '2012-10-08', '2012-10-08', NULL, NULL, NULL, NULL, 1751),
('script.module.xmltodict', 'xmltodict', 'Tristan Fischer (sphere@dersphere.de)', '0.2.0', 'xmltodict is a Python module that makes working with XML feel like you are working with JSON, as in this "spec".[CR]Original: https://github.com/martinblech/xmltodict[CR]Author: martinblech@gmail.com', '2012-10-08', '2012-10-08', NULL, NULL, NULL, NULL, 10067),
('plugin.video.redux_com', 'Redux.com', 'AddonScriptorDE', '1.0.2', 'Hundreds of channels of entertaining, hand-picked video, all from a passionate community of curators.', '2012-10-11', '2012-11-04', 'http://forum.xbmc.org/showthread.php?tid=142465', NULL, NULL, 'https://github.com/AddonScriptorDE/plugin.video.redux_com', 7879),
('plugin.video.neterratv', 'Neterra.TV', 'mr.olix@gmail.com', '0.0.6', '\n    This plugin provides access to tv videos streams from Neterra.TV.\n    Note: Neterra.TV account is required.\n    ', '2012-10-15', '2012-10-15', NULL, NULL, NULL, NULL, 438),
('plugin.image.cheezburger_network', 'Cheezburger Network', 'Tristan Fischer (sphere@dersphere.de)', '0.0.1', 'Cheezburger: All your funny in one place', '2012-10-15', '2012-10-15', NULL, NULL, NULL, NULL, 9192),
('plugin.video.goldpagemedia', 'GoldPageMedia', 'GoldPageMedia', '1.1.1', 'This is free plugin, used for XBMC to watching so much of online TV channels. Website: www.goldpagemedia.com', '2012-10-22', '2012-11-04', NULL, NULL, NULL, NULL, 6565),
('script.module.chardet', 'chardet', 'Valentin-Costel Hăloiu', '2.1.1', 'Packed for XBMC from https://github.com/erikrose/chardet', '2012-11-04', '2012-11-04', NULL, NULL, NULL, NULL, NULL),
('plugin.audio.jazzradio.com', 'JazzRadio.com', 'qualisoft.dk - Tim C. Steinmetz', '1.0.0', '\n			Presenting jazz radio channels for your enjoyment. Listen for free and enjoy countless hours of the best jazz music around\n		', '2012-11-04', '2012-11-04', NULL, NULL, NULL, NULL, 4377),
('plugin.video.itunes_podcasts', 'Apple iTunes Podcasts', 'Tristan Fischer (sphere@dersphere.de)', '1.0.1', 'This add-on lets you browse and search for thousands of free Podcasts.[CR]It provides both, audio and video podcasts - each devided into over 60 genres. You can also search for specific podcasts by name.[CR]It is also possible to manage a "My Podcasts" list via context-menu.', '2012-11-04', '2012-11-06', NULL, NULL, NULL, NULL, 17508),
('script.module.feedparser', 'feedparser', 'Tristan Fischer (sphere@dersphere.de)', '5.1.2', 'feedparser is a Python library that parses feeds in all known formats, including Atom, RSS, and RDF. It runs on Python 2.4 all the way up to 3.2.', '2012-11-04', '2012-11-04', NULL, NULL, NULL, NULL, 10505),
('plugin.video.railscasts', 'Railscasts', 'Pawel Lenart (http://github.com/hrwath)', '0.2', 'RailsCasts is produced by Ryan Bates (rbates on Twitter and ryanb on GitHub). A free episode will be released every other week featuring tips and tricks with Ruby on Rails. The screencasts are short and focus on one technique so you can quickly move on to applying it to your own project. The topics target the intermediate Rails developer, but beginners and experts will get something out of it as well. This add-on is unofficial, in case of any support contact the author directly (via http://github.com/hrwath)', '2012-11-04', '2012-11-04', NULL, NULL, NULL, NULL, 1313),
('plugin.audio.shoutcast', 'Shoutcast 2', 'Tristan Fischer (sphere@dersphere.de)', '2.0.1', 'With this Add-on you can browse more than 50.000 free internet radio stations. Current Features:[CR]- Top 500 Stations[CR]- Browse by genre (and subgenre if enabled in settings)[CR]- Search Station by name[CR]- Search Stations by current playing track[CR]- You can manage a "My Stations"-list[CR]- bitrate and amount of listeners visible and sortable[CR]- 500 Stations on each Page (can be changed in the settings)[CR]- uses cache (24h genre, 1h station listings)', '2012-11-06', '2012-11-06', NULL, NULL, NULL, NULL, 25824),
